#!/usr/bin/env ruby

INPUT = <<-EOF

EOF

strings = INPUT.split('0').reject!{ |e| e.length < 5 }

max = 0

five_char_strings = []

strings.each_with_index do |string, i|
  times = string.length
  for j in Range.new(0, times - 5)
    five_char_strings << string[(j)..(j + 4)]
  end
end

five_char_strings.each do |string|
  product = string.split('').map(&:to_i).reduce(:*)
  max = [max, product].max
end

puts max
